mapper.select:
        select
id, name, parent_id, type, menu_url, status, icon, permission_key, show, create_by, create_time, update_by, update_time
        from auth_permission


                and id = #{id}


                and name = #{name}


                and parent_id = #{parentId}


                and type = #{type}


                and menu_url = #{menuUrl}


                and status = #{status}


                and icon = #{icon}


                and permission_key = #{permissionKey}


                and show = #{show}


                and create_by = #{createBy}


                and create_time = #{createTime}


                and update_by = #{updateBy}


                and update_time = #{updateTime}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from auth_permission


        and id = #{id}


        and name = #{name}


        and parent_id = #{parentId}


        and type = #{type}


        and menu_url = #{menuUrl}


        and status = #{status}


        and icon = #{icon}


        and permission_key = #{permissionKey}


        and show = #{show}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}
