mapper.select:
        select
id, user_name, status, avatar, nick_name, email, phone, password, sex, introduce, create_by, create_time, update_by, update_time, is_deleted
        from auth_user


                and id = #{id}


                and user_name = #{userName}


                and status = #{status}


                and avatar = #{avatar}


                and nick_name = #{nickName}


                and email = #{email}


                and phone = #{phone}


                and password = #{password}


                and sex = #{sex}


                and introduce = #{introduce}


                and create_by = #{createBy}


                and create_time = #{createTime}


                and update_by = #{updateBy}


                and update_time = #{updateTime}


                and is_deleted = #{isDeleted}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from auth_user


        and id = #{id}


        and user_name = #{userName}


        and status = #{status}


        and avatar = #{avatar}


        and nick_name = #{nickName}


        and email = #{email}


        and phone = #{phone}


        and password = #{password}


        and sex = #{sex}


        and introduce = #{introduce}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}
