mapper.select:
        select
id, video_title, up_id, video_url, status, cover_url, likes, favorites, create_by, create_time, update_by, update_time, is_deleted, duration
        from video_info


                and id = #{videoInfo.id}


                and video_title = #{videoInfo.videoTitle}


                and up_id = #{videoInfo.upId}


                and video_url = #{videoInfo.videoUrl}


                and status = #{videoInfo.status}


                and cover_url = #{videoInfo.coverUrl}


                and likes = #{videoInfo.likes}


                and favorites = #{videoInfo.favorites}


                and create_by = #{videoInfo.createBy}


                and create_time = #{videoInfo.createTime}


                and update_by = #{videoInfo.updateBy}


                and update_time = #{videoInfo.updateTime}


                and is_deleted = #{videoInfo.isDeleted}


                and duration = #{videoInfo.duration}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from video_info


        and id = #{id}


        and video_title = #{videoTitle}


        and up_id = #{upId}


        and video_url = #{videoUrl}


        and status = #{status}


        and cover_url = #{coverUrl}


        and likes = #{likes}


        and favorites = #{favorites}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and duration = #{duration}
