mapper.select:
        select
id, video_id, category_id, id_deleted
        from video_category_mapping


                and id = #{id}


                and video_id = #{videoId}


                and category_id = #{categoryId}


                and id_deleted = #{idDeleted}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from video_category_mapping


        and id = #{id}


        and video_id = #{videoId}


        and category_id = #{categoryId}


        and id_deleted = #{idDeleted}
