mapper.select:
        select
id, video_id, tag_id, is_deleted
        from video_tag_mapping


                and id = #{id}


                and video_id = #{videoId}


                and tag_id = #{tagId}


                and is_deleted = #{isDeleted}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from video_tag_mapping


        and id = #{id}


        and video_id = #{videoId}


        and tag_id = #{tagId}


        and is_deleted = #{isDeleted}
